CREATE TABLE IF NOT EXISTS `AccountStatuses` ( `AccountStatusID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT NOT NULL, `Description` TEXT );
CREATE TABLE IF NOT EXISTS `AccountTypes` ( `AccountTypeID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT, `Description` TEXT NOT NULL );
CREATE TABLE IF NOT EXISTS `Accounts` ( `AccountID` INTEGER PRIMARY KEY AUTOINCREMENT, `CustomerID` INTEGER NOT NULL, `Number` TEXT NOT NULL, `AccountTypeID` INTEGER NOT NULL, `AccountStatusID` INTEGER NOT NULL, `OpenDate` DATETIME NOT NULL, `Balance` TEXT NOT NULL, `DebitLine` TEXT, `Interest` INTEGER NOT NULL );
CREATE TABLE IF NOT EXISTS `CardStatuses` ( `CardStatusID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT NOT NULL, `Description` TEXT );
CREATE TABLE IF NOT EXISTS `Customers` ( `CustomerID` INTEGER PRIMARY KEY AUTOINCREMENT, `FirstName` TEXT NOT NULL, `LastName` TEXT NOT NULL, `Login` TEXT NOT NULL, `Password` TEXT NOT NULL, `CreateDate` DATETIME NOT NULL, `IsActive` INTEGER NOT NULL, `LastLogin` DATETIME );
CREATE TABLE IF NOT EXISTS `CardTypes` ( `CardTypeID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT NOT NULL, `Description` TEXT );
CREATE TABLE IF NOT EXISTS `Cards` ( `CardID` INTEGER PRIMARY KEY AUTOINCREMENT, `CardNumber` TEXT NOT NULL, `CardTypeID` INTEGER NOT NULL, `Validity` DATETIME NOT NULL, `BuyingLimit` TEXT NOT NULL, `CashWithdrawLimit` TEXT NOT NULL, `Limit` TEXT, `AccountID` INTEGER NOT NULL );
CREATE TABLE IF NOT EXISTS `TransactionStatuses` ( `TransactionStatusID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT, `Description` TEXT NOT NULL );
CREATE TABLE IF NOT EXISTS `TransactionTypes` ( `TransactionTypeID` INTEGER PRIMARY KEY AUTOINCREMENT, `Name` TEXT NOT NULL, `Description` TEXT );
CREATE TABLE IF NOT EXISTS `Transactions` ( `TransactionID` INTEGER PRIMARY KEY AUTOINCREMENT, `DateOfTransaction` DATETIME NOT NULL, `TransactionTypeID` INTEGER NOT NULL, `Value` TEXT NOT NULL, `Description` TEXT NOT NULL, `TransactionStatusID` INTEGER NOT NULL, `SourceAccountID` INTEGER NOT NULL, `SourceCardID` INTEGER, `DestinationAccountID` INTEGER );